select.if:
SELECT count(*)
FROM board
WHERE DATE_SUB(now(), interval ${lately} day)  < wDate;

mapper.update:
UPDATE board
SET good = good + 1 
WHERE idx = #{idx};

mapper.select:
SELECT * , count(*) as replyCnt
FROM boardReply
WHERE boardIdx = #{idx} order by idx;

SELECT max(levelOrder)
FROM boardReply
WHERE boardIdx = #{boardIdx};

mapper.update:
UPDATE boardReply 
SET levelOrder = levelOrder + 1 
WHERE boardIdx = #{rVo.boardIdx} and levelOrder  >  #{rVo.levelOrder};

